<?phpclass CI_HelpLybrary {    public function __construct() {        $this->_CI_Instance = & get_instance();    }    private function getCI() {        return $this->_CI_Instance;    }     public function generar_notificaciones($num,$data){        switch($num){		case 1://en caso de producto agotado en almacen			$datos=array(								'id_user'=>$data['id_user'],								'name_user'=>$data['nombre'],								'asunto'=>'Producto agotado en Almacen',								'contenido'=>'El producto '.$data['nomb_prod'].' se ha agotado en el almacen '.$data['nomb_alm'].' '		);		break;		}		return $datos;    }     }